import React, { useState } from 'react';
import { AppstoreOutlined, UserOutlined } from '@ant-design/icons';
import { Menu } from 'antd';
const items = [
  {
    key: '1',
    icon: <UserOutlined/>,
    label: 'Người Dùng',
    children: [
      {
        key: '11',
        label: 'Option 1',
      },
      {
        key: '12',
        label: 'Option 2',
      },
      {
        key: '13',
        label: 'Option 3',
      },
      {
        key: '14',
        label: 'Option 4',
      },
    ],
  },
  {
    key: '2',
    icon: <AppstoreOutlined />,
    label: 'Navigation Two',
    children: [
      {
        key: '21',
        label: 'Option 1',
      },
      {
        key: '22',
        label: 'Option 2',
      },
      {
        key: '23',
        label: 'Submenu',
        children: [
          {
            key: '231',
            label: 'Option 1',
          },
          {
            key: '232',
            label: 'Option 2',
          },
          {
            key: '233',
            label: 'Option 3',
          },
        ],
      },
    ],
  },
  
];
const getLevelKeys = (items1) => {
  const key = {};
  const func = (items2, level = 1) => {
    items2.forEach((item) => {
      if (item.key) {
        key[item.key] = level;
      }
      if (item.children) {
        func(item.children, level + 1);
      }
    });
  };
  func(items1);
  return key;
};
const levelKeys = getLevelKeys(items);

export const AdminPage = () => {
  const [stateOpenKeys, setStateOpenKeys,] = useState(['1']);
  const [setKeyselected, keyselected] = useState(null);
  
  const onOpenChange = (openKeys) => {
    const currentOpenKey = openKeys.find((key) => stateOpenKeys.indexOf(key) === -1);
    // open
    if (currentOpenKey !== undefined) {
      const repeatIndex = openKeys
        .filter((key) => key !== currentOpenKey)
        .findIndex((key) => levelKeys[key] === levelKeys[currentOpenKey]);
      setStateOpenKeys(
        openKeys
          // remove repeat key
          .filter((_, index) => index !== repeatIndex)
          // remove current level all child
          .filter((key) => levelKeys[key] <= levelKeys[currentOpenKey]),
      );
    } else {
      // close
      setStateOpenKeys(openKeys);
    }
  };

  const handleOnClick = (e) => {
    console.log('click event', e);

    // Kiểm tra nếu e và e.key tồn tại
    if (e && e.key) {
      console.log('Item clicked with key:', e.key);
      setKeyselected(e.key); // Cập nhật keySelected khi người dùng chọn menu item
    } else {
      console.error("Invalid event data", e);
    }
  };

  return (
    <div style={{display: 'flex'}}>
    <Menu
      mode="inline"
      openKeys={stateOpenKeys}
      onOpenChange={onOpenChange}
      style={{
        width: 256,
      }}
      items={items}
      onClick={handleOnClick}
    />
      <div>
        <p>Item Selected Key: {keyselected}</p>
      </div>
    </div>
  );
}

export default AdminPage
